// SPDX-License-Identifier: MIT
pragma solidity >=0.4.22 <0.9.0;

contract JogoDaVelha {
    uint32 public count = 0;
    Game[] public games;

    event GameHasCreated(uint32 count, address owner, string game_name);

    struct Game {
        address owner;
        string name;
        address oponent;
    }

    // function gameOpen() public return([]){
    //     Game[] memory _games;
    //     return _games;
    // }

    function createGame(string memory _name) public returns (uint32) {
        count = count + 1;
        Game memory game = Game(msg.sender, _name, address(0));
        games.push(game);
        emit GameHasCreated(count, msg.sender, _name);
        return count;
    }
}
